import { useContext, useState, useEffect } from "react";
import { Link, useHistory } from "react-router-dom";

import { API, setAuthToken } from "../config/api";
import { useMutation } from "react-query";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";

import { AuthContext } from "../contexts/authContext";
import { Button, Form, Modal, Alert } from "react-bootstrap";

const phoneRegExp = /^((\\+[1-9]{1,4}[ \\-]*)|(\\([0-9]{2,3}\\)[ \\-]*)|([0-9]{2,4})[ \\-]*)*?[0-9]{3,4}?[ \\-]*[0-9]{3,4}?$/;

const schema = yup.object().shape({
	email: yup.string().email().required(),
	password: yup.string().min(8).required(),
	fullname: yup.string().min(2).required(),
	phone: yup.string().matches(phoneRegExp),
});

function Register() {
	const router = useHistory();
	const [state, dispatch] = useContext(AuthContext);

	const handleClose = () => {
		dispatch({
			type: "MODAL_REGISTER_CLOSE",
		});
	};
	const handleOpenLogin = () => {
		handleClose();
		dispatch({
			type: "MODAL_LOGIN_OPEN",
		});
	};
	const handleOpenRegister = () => {
		handleClose();
		dispatch({
			type: "MODAL_REGISTER_OPEN",
		});
	};

	const {
		register,
		handleSubmit,
		formState: { errors },
		reset,
	} = useForm({
		resolver: yupResolver(schema),
	});

	const onSubmit = useMutation(async (data) => {
		const config = {
			headers: {
				"Content-Type": "application/json",
			},
		};
		console.log(data);
		const body = JSON.stringify({
			email: data.email,
			fullName: data.fullname,
			password: data.password,
			phone: data.phone,
			gender: data.gender,
		});
		const response = await API.post("/register", body, config);

		console.log("res-akhir", response.data?.data?.user?.token);

		setAuthToken(response.data?.data?.user?.token);
		dispatch({
			type: "LOGIN_SUCCESS",
			payload: response.data?.data?.user,
		});
		handleClose();
		router.push("/");
		reset();
		return response;
	});

	// alert
	const [visibleAlert, setVisibleAlert] = useState(false);

	useEffect(() => {
		if (onSubmit.error?.response?.data?.message) {
			setVisibleAlert(true);
			setTimeout(() => {
				setVisibleAlert(false);
			}, 2000);
		}
	}, [onSubmit.error?.response?.data?.message]);

	return (
		<Modal show={state.modalRegister} onHide={handleClose} size="sm" centered>
			<Modal.Body>
				<div className="form-title mb-3">
					<h4 className="text-yellow">Register</h4>
				</div>
				<Alert
					variant="danger"
					show={visibleAlert}
					onClose={() => setVisibleAlert(false)}
					dismissible
				>
					{onSubmit.error?.response?.data?.message}
				</Alert>
				<div className="d-flex flex-column">
					{/* <Form onSubmit={(e) => registerUser(e)}> */}
					<Form onSubmit={handleSubmit(onSubmit.mutate)}>
						<Form.Group controlId="formBasicEmail">
							<Form.Control
								type="email"
								className="form-control input-bg"
								placeholder="Email"
								{...register("email")}
							/>
							<div className="invalid-feedback" style={{ display: "contents" }}>
								{errors.email?.message}
							</div>
						</Form.Group>
						<Form.Group controlId="formBasicPassword">
							<Form.Control
								type="password"
								{...register("password")}
								className="form-control input-bg"
								placeholder="Password"
							/>
							<div className="invalid-feedback" style={{ display: "contents" }}>
								{errors.email?.message}
							</div>
						</Form.Group>
						<Form.Group>
							<Form.Control
								type="text"
								className="form-control input-bg"
								placeholder="Full Name"
								{...register("fullname")}
							/>
							<div className="invalid-feedback" style={{ display: "contents" }}>
								{errors.fullname?.message}
							</div>
						</Form.Group>
						<Form.Group>
							<Form.Control
								as="select"
								custom
								className="form-control input-bg"
								placeholder="Gender"
								required
								{...register("gender")}
							>
								<option value="female">female</option>
								<option value="male">male</option>
							</Form.Control>
						</Form.Group>
						<Form.Group>
							<Form.Control
								type="text"
								className="form-control input-bg"
								placeholder="Phone"
								{...register("phone")}
							></Form.Control>
							<div className="invalid-feedback" style={{ display: "contents" }}>
								{errors.phone?.message}
							</div>
						</Form.Group>
						<Button
							type="submit"
							variant="brown"
							className="btn btn-block btn-round"
							// onClick={registerUser()}
